        } finally {
            lock.writeLock().unlock();
        }
    }

    public int writeBlock(ByteBufferView buffer, long offset) {
        ensureOpen();
        lock.writeLock().lock();
        try {
            if (offset + buffer.remaining() > capacity) {
                return -1;
            }
            return fc.write(buffer, offset);
        } finally {
            lock.writeLock().unlock();
        }
    }

    public long capacity() {
        return capacity;
    }

    public long size() throws IOException {
        ensureOpen();
        return fc.size();
    }

    public void flush() throws IOException {
        ensureOpen();
        fc.force(true);
    }

    public void lockForIoOperation() {
        lock.readLock().lock();
    }

    public void unlockForIoOperation() {
        lock.readLock().unlock();
    }

    public void close() throws IOException {
        if (fc != null) {
            fc.close();
        }
    }

    private void ensureOpen() {
        if (fc == null || !fc.isOpen()) {
            throw new IllegalStateException("File channel is not open");
        }
    }
}

