import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.util.InsecureTrustManagerFactory;
import io.netty.util.internal.SocketUtils;

public class HttpUploadClient {

    private final String host;
    private final int port;
    private final boolean ssl;
    private final SslContext sslCtx;
    private final EventLoopGroup group;
    private final Bootstrap bootstrap;

    public HttpUploadClient(String host, int port, boolean ssl) throws Exception {
        this.host = host;
        this.port = port;
        this.ssl = ssl;

        if (ssl) {
            this.sslCtx = SslContextBuilder.forClient()
                .trustManager(InsecureTrustManagerFactory.INSTANCE).build();
        } else {
            this.sslCtx = null;
        }

        this.group = new NioEventLoopGroup();
        this.bootstrap = new Bootstrap();
        bootstrap.group(group)
                .channel(NioSocketChannel.class)
                .handler(new HttpUploadClientInitializer(sslCtx));
    }

    public Channel connect() throws InterruptedException {
        ChannelFuture future = bootstrap.connect(SocketUtils.socketAddress(host, port));
        return future.sync().channel();
    }

    public void shutdown() {
        group.shutdownGracefully();
    }

    public static void main(String[] args) throws Exception {
        String host = "127.0.0.1";
        int port = 8080;
        boolean ssl = false;

        HttpUploadClient client = new HttpUploadClient(host, port, ssl);
        try {
            Channel channel = client.connect();
            // Perform operations with the channel here
        } finally {
            client.shutdown();
        }
    }
}

