    private List<Protocol> protocolList;

    public RemoteServer(int port) {
        this.port = port;
        this.protocolList = new ArrayList<>();
    }

    public void listen(int port) {
        this.port = port;
    }

    public void start() {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 public void initChannel(SocketChannel ch) throws Exception {
                     ch.pipeline().addLast(new HttpServerCodec());
                     ch.pipeline().addLast(new HttpObjectAggregator(65536));
                     ch.pipeline().addLast(new HttpRequestHandler());
                     ch.pipeline().addLast(new ProtocolHandler());
                 }
             })
             .option(ChannelOption.SO_BACKLOG, 128)
             .childOption(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture f = b.bind(port).sync();
            f.channel().closeFuture().sync();
        } catch (InterruptedException e) {
            e.printStackTrace();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    public Protocol findProtocolByBrowser(ChannelHandlerContext ctx) {
        for (Protocol protocol : protocolList) {
            if (protocol.getBrowserContext().equals(ctx)) {
                return protocol;
            }
        }
        return null;
    }

    public Protocol findProtocolByKey(String key) {
        for (Protocol protocol : protocolList) {
            if (protocol.getKey().equals(key)) {
                return protocol;
            }
        }
        return null;
    }

    public Protocol findProtocolByClient(ChannelHandlerContext ctx) {
        for (Protocol protocol : protocolList) {
            if (protocol.getClientContext().equals(ctx)) {
                return protocol;
            }
        }
        return null;
    }
}

