            String uri = msg.uri();
            if (uri.equals("/api/v1/health")) {
                FullHttpResponse response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.OK);
                response.content().writeBytes("OK".getBytes(StandardCharsets.UTF_8));
                ctx.writeAndFlush(response).addListener(ChannelFutureListener.CLOSE);
            } else {
                bizThreadPool.execute(() -> {
                    try {
                        String result = executorBiz.processRequest(uri);
                        FullHttpResponse response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.OK);
                        response.content().writeBytes(result.getBytes(StandardCharsets.UTF_8));
                        ctx.writeAndFlush(response).addListener(ChannelFutureListener.CLOSE);
                    } catch (Exception e) {
                        sendError(ctx, HttpResponseStatus.INTERNAL_SERVER_ERROR);
                    }
                });
            }
        }

        private void sendError(ChannelHandlerContext ctx, HttpResponseStatus status) {
            FullHttpResponse response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, status);
            response.content().writeBytes(status.reasonPhrase().getBytes(StandardCharsets.UTF_8));
            ctx.writeAndFlush(response).addListener(ChannelFutureListener.CLOSE);
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            cause.printStackTrace();
            ctx.close();
        }

        @Override
        public void userEventTriggered(ChannelHandlerContext ctx, Object evt) throws Exception {
            if (evt instanceof IdleStateEvent) {
                IdleStateEvent event = (IdleStateEvent) evt;
                if (event.state() == IdleState.READER_IDLE) {
                    System.out.println("Connection idle due to no read activity");
                    ctx.close();
                }
            }
            super.userEventTriggered(ctx, evt);
        }
    }
}

