import javax.jms.Connection;
import javax.jms.ConnectionFactory;
import javax.jms.Destination;
import javax.jms.JMSException;
import javax.jms.MessageConsumer;
import javax.jms.Session;
import javax.jms.TextMessage;
import org.apache.activemq.ActiveMQConnection;
import org.apache.activemq.ActiveMQConnectionFactory;

public class JmsConsumer {

    private static final String USERNAME = ActiveMQConnection.DEFAULT_USER;
    private static final String PASSWORD = ActiveMQConnection.DEFAULT_PASSWORD;
    private static final String BROKEURL = ActiveMQConnection.DEFAULT_BROKER_URL;

    private ConnectionFactory connectionFactory;
    private Connection connection;
    private Session session;
    private Destination destination;
    private MessageConsumer messageConsumer;

    public void consumeQueueMessages() throws JMSException {
        connectionFactory = new ActiveMQConnectionFactory(USERNAME, PASSWORD, BROKEURL);
        connection = connectionFactory.createConnection();
        connection.start();
        session = connection.createSession(false, Session.AUTO_ACKNOWLEDGE);
        destination = session.createQueue("SAMPLE_MQ_QUEUE");
        messageConsumer = session.createConsumer(destination);

        while (true) {
            TextMessage textMessage = (TextMessage) messageConsumer.receive(100000);
            if (textMessage != null) {
                System.out.println("QUEUE\t收到的消息:" + textMessage.getText());
            } else {
                break;
            }
        }
    }

    public void consumeTopicMessages() throws JMSException {
        destination = session.createTopic("SAMPLE_MQ_TOPIC");
        messageConsumer = session.createConsumer(destination);

        while (true) {
            TextMessage textMessage = (TextMessage) messageConsumer.receive(100000);
            if (textMessage != null) {
                System.out.println("TOPIC\t收到的消息:" + textMessage.getText());
            } else {
                break;
            }
        }
    }

    public void closeConnection() {
        if (connection != null) {
            try {
                connection.close();
            } catch (JMSException e) {
                e.printStackTrace();
            }
        }
    }
}

