    }

    private void createOutputFile() throws IOException {
        if (append) {
            fileChannel = Files.newByteChannel(Paths.get(filePath), StandardOpenOption.CREATE, StandardOpenOption.WRITE, StandardOpenOption.READ);
        } else {
            fileChannel = Files.newByteChannel(Paths.get(filePath), StandardOpenOption.CREATE, StandardOpenOption.WRITE);
        }
    }

    public boolean writeTag(ITag tag) {
        try {
            ByteBuffer buffer = ByteBuffer.allocate(tag.getSize());
            tag.write(buffer);
            buffer.flip();
            dataChannel.write(buffer);
            return true;
        } catch (IOException e) {
            e.printStackTrace();
            return false;
        }
    }

    public void close() {
        try {
            finalizeFlv();
            if (dataChannel != null) {
                dataChannel.close();
            }
            if (fileChannel != null) {
                fileChannel.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

