                for (ConsumerRecord<byte[], byte[]> record : records) {
                    if (topicPartition != null && !topicPartition.equals(record.partition())) {
                        continue;
                    }
                    K key = serializer.deserialize(record.key());
                    V value = serializer.deserialize(record.value());
                    if (storeUpdateHandler.validate(key, value)) {
                        localStore.update(key, value);
                    }
                    offsetInSchemasTopic = record.offset();
                    offsetUpdateLock.lock();
                    try {
                        offsetReachedThreshold.signal();
                    } finally {
                        offsetUpdateLock.unlock();
                    }
                }
            }
        } catch (WakeupException e) {
            // Handle shutdown
        } finally {
            consumer.close();
        }
    }

    public void doWork() {
        run();
    }

    public void shutdown() {
        consumer.wakeup();
        try {
            Thread.sleep(1000);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }
}

