        if (sendFuture.isDone()) {
            try {
                sendFuture.get();
            }
            catch (InterruptedException e) {
                Thread.currentThread().interrupt();
                throw new KafkaException("Interrupted", e);
            }
            catch (ExecutionException e) {
                throw new KafkaException("Send failed", e.getCause());
            }
        }
        if (this.autoFlush) {
            producer.flush();
        }
        return future;
    }

    public List<PartitionInfo> partitionsFor(String topic) {
        Producer<K, V> producer = getTheProducer();
        try {
            return producer.partitionsFor(topic);
        }
        finally {
            closeProducer(producer, inTransaction());
        }
    }

    public Map<MetricName, ? extends Metric> metrics() {
        Producer<K, V> producer = getTheProducer();
        try {
            return producer.metrics();
        }
        finally {
            closeProducer(producer, inTransaction());
        }
    }

    protected void closeProducer(Producer<K, V> producer, boolean inTx) {
        if (!inTx) {
            producer.close();
        }
    }

    protected Producer<K, V> getTheProducer() {
        return getTheProducer(null);
    }

    protected Producer<K, V> getTheProducer(@Nullable String topic) {
        if (this.transactional) {
            Assert.state(inTransaction(),
                    "No transaction is in process; possible solutions: run the template operation within the scope of a template.executeInTransaction() operation");
            return this.producerFactory.createProducer();
        }
        else {
            return this.producerFactory.createProducer();
        }
    }

    public boolean inTransaction() {
        return this.transactional && TransactionSynchronizationManager.getResource(this.producerFactory) != null;
    }
}

