        try {
            return stdout.ready();
        } catch (java.io.IOException e) {
            Log.e("AFWall", "stdoutAvailable error", e);
            return false;
        }
    }

    public String readStdoutBlocking() {
        String line;
        if (stdout == null) {
            return null;
