        synchronized (this) {
          byte[] buf = new byte[4096];
          int i;
          while ((i = is.read(buf)) != -1) {
            os.write(buf, 0, i);
          }
          if (closeStreams) {
            os.close();
            is.close();
          }
        }
      } catch (IOException e) {
        e.printStackTrace();
      }
    }
  }
}

