        Notifier notifier = notifiers.computeIfAbsent(path, k -> new Notifier(this, path, listener));
        notifier.start();
    }

    private Jedis getJedis() {
        String serverAddress = getPrimaryServerAddress();
        JedisPool pool = jedisPools.get(serverAddress);
        return pool.getResource();
    }

    private String getPrimaryServerAddress() {
        return jedisPools.keySet().iterator().next();
    }

    private void replicateToAllServers(String key, String value) {
        for (String serverAddress : jedisPools.keySet()) {
            try (Jedis jedis = jedisPools.get(serverAddress).getResource()) {
                jedis.set(key, value);
            } catch (Exception e) {
                // Handle replication failure
            }
