        @Override
        public void onMessage(String channel, String message) {
            try {
                Node node = Node.deserialize(message);
                if (node.getPath().equals(path)) {
                    listener.onNotify(node);
                }
            } catch (Exception e) {
                // Handle deserialization or notification error
            }
        }
    }

    static class Notifier extends Thread {
        private final RedisRegistry registry;
        private final String path;
        private final NotifyListener listener;
        private volatile boolean running = true;

        public Notifier(RedisRegistry registry, String path, NotifyListener listener) {
            this.registry = registry;
            this.path = path;
            this.listener = listener;
        }

        @Override
        public void run() {
            try (Jedis jedis = registry.getJedis()) {
                String channel = registry.clusterName + ":" + path;
                NotifySub sub = new NotifySub(registry, path, listener);
                jedis.psubscribe(sub, channel);
            } catch (Exception e) {
                // Handle subscription error
            }
        }

        public void shutdown() {
            running = false;
            interrupt();
        }
    }

    static class Node {
        private final String id;
        private final String path;
