import io.lettuce.core.resource.Transports;
import io.lettuce.test.resource.TestClientResources;
import io.netty.util.internal.SystemPropertyUtil;
import java.io.File;
import java.io.IOException;
import java.util.Locale;

public class RedisSocketConnector {

    private RedisClient redisClient;
    private StatefulRedisConnection<String, String> connection;

    public void connect(RedisURI redisURI) {
        redisClient = RedisClient.create(TestClientResources.get(), redisURI);
        connection = redisClient.connect();
    }

    public void performRedisAction(String key, String value) {
        RedisCommands<String, String> syncCommands = connection.sync();
        syncCommands.set(key, value);
        String result = syncCommands.get(key);
    }

    public void close() {
        if (connection != null) {
            connection.close();
        }
        if (redisClient != null) {
            redisClient.shutdown();
        }
    }

    public static RedisURI createSocketRedisUri(String socketPath) throws IOException {
        File file = new File(socketPath).getCanonicalFile();
        return RedisURI.create(RedisURI.URI_SCHEME_REDIS_SOCKET + "://" + file.getCanonicalPath());
    }

    public static boolean isSupported() {
        String osName = SystemPropertyUtil.get("os.name").toLowerCase(Locale.UK).trim();
        return Transports.NativeTransports.isDomainSocketSupported();
    }
}

