    public RMQAsyncSendProducer(String nsAddr, String topic) {
        this.nsAddr = nsAddr;
        this.topic = topic;
        this.logger = LoggerFactory.getLogger(RMQAsyncSendProducer.class);
    }

    public void create() {
        producer = new DefaultMQProducer("AsyncSendProducerGroup");
        producer.setNamesrvAddr(nsAddr);
        sendCallback = new SendCallback() {
            @Override
            public void onSuccess(SendResult sendResult) {
                successSendResult.add(sendResult);
                logger.info("Message sent successfully: {}", sendResult.getMsgId());
            }

            @Override
            public void onException(Throwable throwable) {
                logger.error("Message sending failed: ", throwable);
                exceptionMsgCount.incrementAndGet();
            }
        };
    }

    public void start() {
        try {
            producer.start();
        } catch (MQClientException e) {
            logger.error("Failed to start producer", e);
        }
    }

    public void asyncSend(Object msg) {
        Message message = new Message(topic, msg.toString().getBytes());
        producer.send(message, sendCallback);
    }

    public void asyncSend(int msgSize) {
        this.msgSize = msgSize;
        for (int i = 0; i < msgSize; i++) {
            Object msg = originMsgs.get(i);
            asyncSend(msg);
        }
    }

    public void asyncSend(Object msg, MessageQueueSelector selector, Object arg) {
        Message message = new Message(topic, msg.toString().getBytes());
        producer.send(message, selector, arg, sendCallback);
    }

    public void waitForResponse(int timeoutMills) {
        long startTime = System.currentTimeMillis();
        while (System.currentTimeMillis() - startTime < timeoutMills && !successSendResult.isEmpty() && successSendResult.size() != msgSize) {
            try {
                Thread.sleep(100);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
                logger.error("Interrupted during wait for response", e);
            }
        }
        if (successSendResult.size() != msgSize) {
            logger.warn("Not all messages were successfully sent. Failed count: {}", exceptionMsgCount.get());
        }
    }

    public void shutdown() {
        producer.shutdown();
    }
}

