import java.net.InetSocketAddress;
import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpClient.Redirect;
import java.net.http.HttpRequest;
import java.net.http.HttpRequest.BodyPublishers;
import java.net.http.HttpRequest.Builder;
import java.net.http.HttpResponse;
import java.net.http.HttpResponse.BodyHandlers;
import java.util.ArrayList;
import java.util.List;

public class HttpCommunicationClient {

    protected InetSocketAddress address;
    protected HttpClient httpClient;

    public void setupHttpClient(ThreadPool threadPool) {
        httpClient = HttpClient.newBuilder()
            .followRedirects(Redirect.NORMAL)
            .executor(threadPool.generic())
            .build();
    }

    public void closeClient() {
        httpClient.close();
    }

    private HttpResponse<String> executeAndDefaultAssertions(HttpRequest request) throws Exception {
        var resp = httpClient.send(request, BodyHandlers.ofString());
        return resp;
    }

    public HttpResponse<String> get(String path, String[] ... headers) throws Exception {
        URI uri = URI.create(String.format("http://%s:%s%s", address.getHostName(), address.getPort(), path));
        Builder builder = HttpRequest.newBuilder(uri);
        for (String[] header : headers) {
            builder.header(header[0], header[1]);
        }
        return executeAndDefaultAssertions(builder.build());
    }

    public HttpResponse<String> browserGet(String uri) throws Exception {
        return get(uri, browserHeader());
    }

    public HttpResponse<String> post(String path) throws Exception {
        URI uri = URI.create(String.format("http://%s:%s%s", address.getHostName(), address.getPort(), path));
        HttpRequest request = HttpRequest.newBuilder(uri)
            .POST(BodyPublishers.noBody())
            .build();
        return executeAndDefaultAssertions(request);
    }

    public List<String> getAllRedirectLocations(String path, String[] ... headers) throws Exception {
        URI uri = URI.create(String.format("http://%s:%s%s", address.getHostName(), address.getPort(), path));
        Builder builder = HttpRequest.newBuilder(uri);
        for (String[] header : headers) {
            builder.header(header[0], header[1]);
        }
        var response = httpClient.send(builder.build(), BodyHandlers.discarding());
        response = response.previousResponse().orElse(null);
        List<String> redirects = new ArrayList<>();
        while (response != null) {
            redirects.addAll(response.headers().allValues("location"));
