        if (!state.isConnected() || state.isClosed()) {
            return false;
        }
        if (packetForClient != null) {
            return false;
        }
        return getRemainingClientWindow() > 0;
    }

    private boolean mayWrite() {
        return !clientToNetwork.isEmpty();
    }

    private boolean mayConnect() {
        return state == State.SYN_SENT;
    }
}

