                    logger.warn("Connection failed for: {}", channel.getRemoteAddress());
                    key.cancel();
                }
            } else if (key.isWritable()) {
                WriteTask task = (WriteTask) key.attachment();
                if (task != null) {
                    ByteBuffer buffer = task.getBuffer();
                    int bytesWritten = 0;
                    while (buffer.hasRemaining()) {
                        bytesWritten += channel.write(buffer);
                    }
                    if (bytesWritten == 0) {
                        logger.warn("Write operation failed for: {}", channel.getRemoteAddress());
                    } else {
                        logger.debug("Wrote {} bytes to: {}", bytesWritten, channel.getRemoteAddress());
                    }
                    key.interestOps(SelectionKey.OP_READ);
                }
            } else if (key.isReadable()) {
                for (KeyProcessor processor : myKeyProcessors) {
                    processor.processRead(key);
                }
            }
        }
    }

    public boolean offerConnect(ConnectTask task, int timeout, TimeUnit unit) {
        return addTaskToQueue(task, timeout, unit, myConnectQueue);
    }

    private boolean addTaskToQueue(T task, int timeout, TimeUnit unit, BlockingQueue<T> queue) {
        try {
            return queue.offer(task, timeout, unit);
        } catch (InterruptedException e) {
            logger.error("Interrupted while adding task to queue", e);
            return false;
        }
    }

    private void processWriteTasks() {
        WriteTask task;
        while ((task = myWriteQueue.poll(100, TimeUnit.MILLISECONDS)) != null) {
            try {
                SocketChannel channel = (SocketChannel) task.getChannel();
                ByteBuffer buffer = task.getBuffer();
                int bytesWritten = 0;
                while (buffer.hasRemaining()) {
                    bytesWritten += channel.write(buffer);
                }
                if (bytesWritten == 0) {
                    logger.warn("Write operation failed for: {}", channel.getRemoteAddress());
                } else {
                    logger.debug("Wrote {} bytes to: {}", bytesWritten, channel.getRemoteAddress());
                }
            } catch (IOException e) {
                logger.error("Failed to write to channel", e);
            }
        }
    }

    private void cleanup() {
        long currentTime = myTimeService.getCurrentTime();
        if (currentTime - lastCleanupTime >= myCleanupTimeoutMillis) {
            lastCleanupTime = currentTime;
            for (KeyProcessor processor : myKeyProcessors) {
                processor.cleanup();
            }
            myCleanupProcessor.cleanup();
        }
    }

    private boolean needRunCleanup() {
        return myTimeService.getCurrentTime() - lastCleanupTime >= myCleanupTimeoutMillis;
    }
}

