import java.nio.ByteBuffer;
import java.nio.channels.DatagramChannel;

public final class MulticastUDP {

    private DatagramChannel channel;
    private InetAddress group;
    private int port;

    public MulticastUDP(String host, int port) throws Exception {
        this.group = InetAddress.getByName(host);
        this.port = port;
        this.channel = DatagramChannel.open(StandardProtocolFamily.INET);
    }

    public void joinGroup() throws Exception {
        channel.bind(new InetSocketAddress(port));
        channel.join(group, Utils.getLocalNetworkInterface());
    }

    public String receive() throws Exception {
        ByteBuffer buffer = ByteBuffer.allocate(1024);
        SocketAddress sender = channel.receive(buffer);
        buffer.flip();
        byte[] data = new byte[buffer.remaining()];
        buffer.get(data);
        return new String(data);
    }

    public void send(String message, String targetHost, int targetPort) throws Exception {
        channel.configureBlocking(true);
        InetSocketAddress sender = new InetSocketAddress(targetHost, targetPort);
        channel.send(ByteBuffer.wrap(message.getBytes()), sender);
    }

    public void close() throws Exception {
        channel.close();
    }
}

