            NetworkInterface networkInterface = NetworkInterface.getByName("eth0");
            if (networkInterface == null) {
                throw new RuntimeException("Network interface not found");
            }
            channel.join(group, networkInterface);
        } catch (Exception e) {
            throw new RuntimeException("Failed to join multicast group", e);
        }
    }

    public String receive() {
        try {
            ByteBuffer buffer = ByteBuffer.allocate(1024);
            SocketAddress socketAddress = channel.receive(buffer);
            if (socketAddress == null) {
                return null;
            }
            buffer.flip();
            byte[] data = new byte[buffer.remaining()];
            buffer.get(data);
            return new String(data, StandardCharsets.UTF_8);
        } catch (Exception e) {
            throw new RuntimeException("Failed to receive message", e);
        }
    }

    public void send(String message, String targetHost, int targetPort) {
        try {
            InetAddress target = InetAddress.getByName(targetHost);
            DatagramPacket packet = new DatagramPacket(message.getBytes(StandardCharsets.UTF_8), message.length(), target, targetPort);
            channel.send(packet);
        } catch (Exception e) {
            throw new RuntimeException("Failed to send message", e);
        }
    }

    public void close() {
        try {
            if (channel != null) {
                channel.close();
            }
        } catch (Exception e) {
            throw new RuntimeException("Failed to close channel", e);
        }
    }
}

