            if (e instanceof StatusRuntimeException) {
                if (retry < stubProxy.getHostCount()) {
                    synchronized (this) {
                        stubProxy.setStub(null);
                    }
                    streamingCall(method, request, responseObserver, ++retry);
                    return;
                }
            }
            log.error("rpc call with exception, {}", e.getMessage());
        }
    }

