    try {
      return requests.poll(3, TimeUnit.SECONDS);
    } catch (InterruptedException e) {
      Thread.currentThread().interrupt();
      throw new AssertionError(e);
    }
  }

  void enqueueDelay(long millis) {
    this.delayQueue.add(millis);
  }
}

