        this.clientsMap = new ConcurrentHashMap<>();
        this.port = config.getPort();
        try {
            this.serverSocket = new ServerSocket(port);
        } catch (IOException e) {
            onError(e);
        }
        this.waitConnectionThread = new Thread(new WaitRequestsRunnable());
        this.waitConnectionThread.start();
    }

    public void waitForRequest() {
        while (true) {
            try {
                Socket socket = serverSocket.accept();
                socketProcessor.execute(new SocketProcessorRunnable(socket));
            } catch (IOException e) {
                onError(e);
            }
        }
    }

    public void processSocket(Socket socket) {
        try {
            InputStream input = socket.getInputStream();
            OutputStream output = socket.getOutputStream();
            String requestLine = readLine(input);
            String[] parts = requestLine.split(" ");
            String method = parts[0];
            String url = parts[1];
            String httpVersion = parts[2];

            HttpProxyCacheServerClients client = getClients(url);
            client.processRequest(method, url, httpVersion, input, output);
        } catch (IOException e) {
            onError(e);
        } finally {
            releaseSocket(socket);
        }
    }

    public HttpProxyCacheServerClients getClients(String url) {
        synchronized (clientsLock) {
            HttpProxyCacheServerClients client = clientsMap.get(url);
            if (client == null) {
                client = new HttpProxyCacheServerClients(url);
                clientsMap.put(url, client);
            }
            return client;
        }
    }

    public void releaseSocket(Socket socket) {
        closeSocketInput(socket);
        closeSocketOutput(socket);
        closeSocket(socket);
    }

    public void closeSocketInput(Socket socket) {
        try {
            if (socket != null && socket.getInputStream() != null) {
                socket.getInputStream().close();
            }
        } catch (IOException e) {
            onError(e);
        }
    }

    public void closeSocketOutput(Socket socket) {
        try {
            if (socket != null && socket.getOutputStream() != null) {
                socket.getOutputStream().close();
            }
        } catch (IOException e) {
            onError(e);
        }
    }

    public void closeSocket(Socket socket) {
        try {
            if (socket != null) {
                socket.close();
            }
        } catch (IOException e) {
            onError(e);
        }
    }

    public void onError(Throwable e) {
        e.printStackTrace();
    }

    static class WaitRequestsRunnable implements Runnable {
        @Override
        public void run() {
            HttpProxyCacheServer server = new HttpProxyCacheServer(new Context());
            server.waitForRequest();
        }
    }

    static class SocketProcessorRunnable implements Runnable {
        private final Socket socket;

        public SocketProcessorRunnable(Socket socket) {
            this.socket = socket;
        }

        @Override
        public void run() {
            HttpProxyCacheServer server = new HttpProxyCacheServer(new Context());
            server.processSocket(socket);
        }
    }
}

