            if (requestParts.length < 3 || !requestParts[0].equals("GET") || !requestParts[2].startsWith("HTTP/1.1")) {
                out.println("HTTP/1.1 400 Bad Request");
                out.println();
                return;
            }

            String path = requestParts[1];
            if (!path.equals("/ws")) {
                out.println("HTTP/1.1 404 Not Found");
                out.println();
                return;
            }

            String upgradeHeader = "";
            String connectionHeader = "";
            String secWebSocketKey = "";

            String line;
            while ((line = in.readLine()) != null && !line.isEmpty()) {
                if (line.startsWith("Upgrade:")) {
                    upgradeHeader = line.substring("Upgrade:".length()).trim();
                } else if (line.startsWith("Connection:")) {
                    connectionHeader = line.substring("Connection:".length()).trim();
                } else if (line.startsWith("Sec-WebSocket-Key:")) {
                    secWebSocketKey = line.substring("Sec-WebSocket-Key:".length()).trim();
                }
            }

            if (!upgradeHeader.equals("websocket") || !connectionHeader.equals("upgrade") || secWebSocketKey.isEmpty()) {
                out.println("HTTP/1.1 400 Bad Request");
                out.println();
                return;
            }

            String acceptKey = generateAcceptKey(secWebSocketKey);
            String handshakeResponse = "HTTP/1.1 101 Switching Protocols\r\n" +
                    "Upgrade: websocket\r\n" +
                    "Connection: upgrade\r\n" +
                    "Sec-WebSocket-Accept: " + acceptKey + "\r\n\r\n";
            out.print(handshakeResponse);
            out.flush();

            byte[] binaryData = "Hello, WebSocket!".getBytes();
            sendBinaryFrame(out, binaryData, true, 136);

            clientSocket.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private String generateAcceptKey(String key) {
        String guid = "258EAFA5-E914-47DA-95CA-D5845835E41B";
        String combined = key + guid;
        MessageDigest md = MessageDigest.getInstance("SHA1");
        byte[] hash = md.digest(combined.getBytes());
        StringBuilder hexString = new StringBuilder();
        for (byte b : hash) {
            String hex = Integer.toHexString(0xff & b);
            if (hex.length() == 1) hexString.append('0');
            hexString.append(hex);
        }
        return Base64.getEncoder().encodeToString(hexString.toString().getBytes());
    }

    private void sendBinaryFrame(PrintWriter out, byte[] data, boolean mask, int payloadLength) {
        byte[] frame = new byte[2];
        frame[0] = (byte) (0x80 | 0x00); // FIN and Opcode: Binary
        frame[1] = (byte) (mask ? 0x80 : 0x00 | payloadLength);
        out.write(frame[0]);
        out.write(frame[1]);
        out.flush();

        byte[] maskedData = mask ? maskData(data, (byte) 0x12) : data;
        out.write(maskedData);
        out.flush();
    }

    private byte[] maskData(byte[] data, byte mask) {
        byte[] masked = new byte[data.length];
        for (int i = 0; i < data.length; i++) {
            masked[i] = (byte) (data[i] ^ mask);
        }
        return masked;
    }

    public void stop() {
        try {
            if (serverSocket != null) {
                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

