public class WebSocketHandshakeServer {

    private static final String additionalHandshake = "Upgrade: websocket\r\nConnection: Upgrade\r\n\r\n";
    private ServerSocket serverSocket;
    private int port;

    public WebSocketHandshakeServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        serverSocket.setReuseAddress(true);

        new Thread(() -> {
            while (!serverSocket.isClosed()) {
                try {
                    Socket client = serverSocket.accept();
                    handleClientConnection(client);
                } catch (IOException e) {
                    if (!serverSocket.isClosed()) {
                        e.printStackTrace();
                    }
                }
            }
        }).start();
    }

    private void handleClientConnection(Socket client) throws IOException {
        try (Scanner in = new Scanner(client.getInputStream());
             OutputStream os = client.getOutputStream()) {

            String input = in.nextLine();
            String testCase = input.split(" ")[1];

            switch (testCase) {
                case "/0":
                    os.write(asciiBytes("HTTP/1.1 100 Switching Protocols\r\n" + additionalHandshake));
                    break;
                case "/1":
                    os.write(asciiBytes("HTTP/1.0 100 Switching Protocols\r\n" + additionalHandshake));
                    break;
                case "/2":
                    os.write(asciiBytes("HTTP 100 Switching Protocols\r\n" + additionalHandshake));
                    break;
                case "/3":
                    os.write(asciiBytes("HTTP/1.1 200 Switching Protocols\r\n" + additionalHandshake));
                    break;
                case "/4":
                    os.write(asciiBytes("HTTP 101 Switching Protocols\r\n" + additionalHandshake));
                    break;
                case "/5":
                    os.write(asciiBytes("HTTP/1.1 404 Switching Protocols\r\n" + additionalHandshake));
                    break;
                case "/6":
                    os.write(asciiBytes("HTTP/2.0 404 Switching Protocols\r\n" + additionalHandshake));
                    break;
                case "/7":
                    os.write(asciiBytes("HTTP/1.1 500 Switching Protocols\r\n" + additionalHandshake));
                    break;
                case "/8":
                    os.write(asciiBytes("GET 302 Switching Protocols\r\n" + additionalHandshake));
                    break;
                case "/9":
                    os.write(asciiBytes("GET HTTP/1.1 101 Switching Protocols\r\n" + additionalHandshake));
                    break;
                case "/10":
                    os.write(asciiBytes("HTTP/1.1 101 Switching Protocols\r\n" + additionalHandshake));
                    break;
                case "/11":
                    os.write(asciiBytes("HTTP/1.1 101 Websocket Connection Upgrade\r\n" + additionalHandshake));
                    break;
            }
            os.flush();
        } finally {
            client.close();
        }
    }

    public void stop() throws IOException {
        if (serverSocket != null && !serverSocket.isClosed()) {
            serverSocket.close();
        }
    }

    private byte[] asciiBytes(String input) {
        return input.getBytes(java.nio.charset.StandardCharsets.US_ASCII);
    }
}

