import android.util.Log;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.InetSocketAddress;
import java.net.ServerSocket;
import java.net.Socket;

public abstract class NanoHTTPD {
    private final String hostname;
    private final int myPort;
    private ServerSocket myServerSocket;
    private Thread myThread;

    public NanoHTTPD(int port) {
        this(null, port);
    }

    public NanoHTTPD(String hostname, int port) {
        this.hostname = hostname;
        this.myPort = port;
    }

    public void start() throws IOException {
        Log.i("NanoHTTPD", "server start");
        myServerSocket = new ServerSocket();
        myServerSocket.bind((hostname != null) ? new InetSocketAddress(
                hostname, myPort) : new InetSocketAddress(myPort));

        myThread = new Thread(new Runnable() {
            @Override
            public void run() {
                do {
                    try {
                        final Socket finalAccept = myServerSocket.accept();
                        Log.i("NanoHTTPD",
                                "accept request from "
                                        + finalAccept.getInetAddress());
                        InputStream inputStream = finalAccept.getInputStream();
                        OutputStream outputStream = finalAccept
                                .getOutputStream();
                        handleConnection(inputStream, outputStream);
                        if (finalAccept != null) {
                            finalAccept.close();
                        }
                    } catch (IOException e) {
                    }
                } while (!myServerSocket.isClosed());
            }
        });
        myThread.setDaemon(true);
        myThread.setName("NanoHttpd Main Listener");
        myThread.start();
    }

    public void stop() {
        Log.i("NanoHTTPD", "server stop");
        try {
            myServerSocket.close();
            myThread.join();
        } catch (IOException ioe) {
            ioe.printStackTrace();
        } catch (InterruptedException e) {
            e.printStackTrace();
        }
    }

    protected abstract void handleConnection(InputStream inputStream, OutputStream outputStream);
}

