            if (c == '\r') {
                continue;
            }
            sb.append((char) c);
        }
        return sb.toString();
    }

    public int skipLine(InputStream in) throws IOException {
        int c;
        while ((c = in.read()) != -1) {
            if (c == '\n') {
                return 1;
            }
            if (c == '\r') {
                continue;
            }
        }
        return 0;
    }

    public void doReply(InputStream in, OutputStream out, String cmd) throws IOException {
        String[] parts = cmd.split("\\?");
        String path = parts[0];
        if (path.equals("/sendclass")) {
            letUsersSendClassfile(out, parts[1], Integer.parseInt(parts[2]));
        } else {
            sendHeader(out, 0, 200);
            if (path.equals("/")) {
                path = "/index.html";
            }
            File file = new File(path);
            if (file.exists()) {
                FileInputStream fis = new FileInputStream(file);
                byte[] buffer = new byte[4096];
                int bytesRead;
                while ((bytesRead = fis.read(buffer)) != -1) {
                    out.write(buffer, 0, bytesRead);
                }
                fis.close();
            } else {
                replyError(out, new BadHttpRequest("File not found"));
            }
        }
    }

    public boolean letUsersSendClassfile(OutputStream out, String filename, int length) throws IOException {
        ClassPool cp = classPool;
        if (translator != null) {
            cp = cp.makeTransformer(translator);
        }
        CtClass cc = cp.makeClass(filename);
        byte[] bytecode = cc.toBytearray();
        sendHeader(out, bytecode.length, 200);
        out.write(bytecode);
        return true;
    }

    public void sendHeader(OutputStream out, long dataLength, int filetype) throws IOException {
        String header = "HTTP/1.0 200 OK" + endofline;
        header += "Content-Type: text/html" + endofline;
        header += "Content-Length: " + dataLength + endofline;
        header += endofline;
        out.write(header.getBytes());
    }

    public void replyError(OutputStream out, BadHttpRequest e) throws IOException {
        String header = "HTTP/1.0 400 Bad Request" + endofline;
        header += "Content-Type: text/plain" + endofline;
        header += "Content-Length: " + e.getMessage().length() + endofline;
        header += endofline;
        out.write(header.getBytes());
        out.write(e.getMessage().getBytes());
    }
}

class BadHttpRequest extends Exception {
    public BadHttpRequest(String message) {
        super(message);
