public class EmbedHttpServer implements Runnable {
    private int port;
    private ServerSocket serverSocket;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        if (serverSocket == null) {
            serverSocket = new ServerSocket(port);
            new Thread(this, "embed-http-server").start();
        }
    }

    public void stop() throws IOException {
        if (serverSocket != null) {
            serverSocket.close();
            serverSocket = null;
        }
    }

    protected void handle(String method, String path,
            HashMap<String, String> headers, InputStream input,
            OutputStream response) throws Exception {
    }

    @Override
    public void run() {
        final ServerSocket ss = serverSocket;
        while (ss == serverSocket) {
            Socket conn = null;
            try {
                conn = ss.accept();
                String method = null;
                String path = null;
                HashMap<String, String> headers = new HashMap<String, String>();

                InputStream ins = conn.getInputStream();
                StringBuilder sb = new StringBuilder(512);
                int l;
                while ((l = ins.read()) != -1) {
                    if (l == '\n') {
                        if (sb.length() > 0
                                && sb.charAt(sb.length() - 1) == '\r')
                            sb.setLength(sb.length() - 1);
                        if (sb.length() == 0) {
                            break;
                        } else if (method == null) {
                            int i = sb.indexOf(" ");
                            method = sb.substring(0, i);
                            int j = sb.lastIndexOf(" HTTP/");
                            path = sb.substring(i + 1, j).trim();
                        } else {
                            int i = sb.indexOf(":");
                            String name = sb.substring(0, i).trim();
                            String val = sb.substring(i + 1).trim();
                            headers.put(name, val);
                        }
                        sb.setLength(0);
                    } else {
                        sb.append((char) l);
                    }
                }
                int contentLength = 0;
                String str = headers.get("Content-Length");
                if (str != null) {
                    contentLength = Integer.parseInt(str);
                }
                OutputStream os = conn.getOutputStream();
                str = headers.get("Expect");
                if ("100-Continue".equalsIgnoreCase(str)) {
                    os.write("HTTP/1.1 100 Continue\r\n\r\n".getBytes("ASCII"));
                    os.flush();
                }
                InputStream input = new BodyInputStream(ins, contentLength);
                handle(method, path, headers, input, os);
                os.close();

                conn.close();
                conn = null;
            } catch (Exception e) {
                if (conn != null) {
                    try {
                        conn.close();
                    } catch (Exception ee) {
                    }
                }
            }

            if (!ss.isBound() || ss.isClosed()) {
                serverSocket = null;
            }
        }
    }

    private static class BodyInputStream extends InputStream {
        private InputStream ins;
        private int n;

        public BodyInputStream(InputStream ins, int n) {
            this.ins = ins;
            this.n = n;
        }

        @Override
        public int available() throws IOException {
            return n;
        }

        @Override
        public int read() throws IOException {
            if (n <= 0)
                return -1;
            int r = ins.read();
            if (r != -1)
                n--;
            return r;
        }

        @Override
        public int read(byte[] b, int off, int len) throws IOException {
            if (n <= 0)
                return -1;
            int l = ins.read(b, off, len < n ? len : n);
            if (l != -1)
                n -= l;
            return l;
        }

        @Override
        public long skip(long n) throws IOException {
            throw new IOException("unsupported");
        }

        @Override
        public void close() throws IOException {
            ins.close();
        }
    }
