            showErrorDialog("Error closing connection: " + e.getMessage());
        }
    }

    public void readWriteLine(String command) {
        try {
            writeLine(command);
            String response = in.readLine();
            if (response == null || !response.startsWith("250")) {
                showErrorDialog("SMTP server responded with: " + response);
            }
        } catch (Exception e) {
            showErrorDialog("Error reading/writing line: " + e.getMessage());
        }
    }

    public void writeLine(String line) {
        try {
            out.write((line + "\r\n").getBytes());
            out.flush();
        } catch (Exception e) {
            showErrorDialog("Error writing line: " + e.getMessage());
        }
    }

    public String splitRecipientString(String input, List<String> recipients) {
        String[] parts = input.split(",");
        for (String part : parts) {
            recipients.add(part.trim());
        }
        return input;
    }

    public void showErrorDialog(String message) {
        JOptionPane.showMessageDialog(null, message, errorDialogTitle, JOptionPane.ERROR_MESSAGE);
    }
}

