            sock.bind(new InetSocketAddress(bind_addr, 0));
            in = sock.getInputStream();
            out = sock.getOutputStream();
            return (T) this;
        } catch (IOException e) {
            throw new RuntimeException("Failed to start TCP connection", e);
        }
    }

    public T stop() {
        try {
            if (sock != null && sock.isConnected()) {
                sock.close();
            }
            if (in != null) {
                in.close();
            }
            if (out != null) {
                out.close();
            }
        } catch (IOException e) {
            throw new RuntimeException("Failed to stop TCP connection", e);
        }
        return (T) this;
    }

    public boolean isRunning() {
        return sock != null && sock.isConnected();
    }

    public T sendRequest(byte[] request) {
        try {
            if (!isRunning()) {
                throw new IllegalStateException("Connection is not running");
            }
            out.write(request);
            out.flush();
        } catch (IOException e) {
            throw new RuntimeException("Failed to send request", e);
        }
        return (T) this;
    }

    public ByteArray fetchResponse() {
        try {
            if (!isRunning()) {
                throw new IllegalStateException("Connection is not running");
            }
            byte[] buffer = new byte[4096];
            int bytesRead;
            ByteArrayOutputStream responseStream = new ByteArrayOutputStream();
            while ((bytesRead = in.read(buffer)) != -1) {
                responseStream.write(buffer, 0, bytesRead);
            }
            byte[] response = responseStream.toByteArray();
            on_rsp.accept(response);
            return new ByteArray(response);
        } catch (IOException e) {
            throw new RuntimeException("Failed to fetch response", e);
        }
    }
}

