        out.flush();
    }

    private void send(CommandPacket cmd) throws IOException {
        if (isClosed.get()) {
            throw new IllegalStateException("Channel is closed");
        }
        byte[] data = cmd.arguments;
        byte[] packet = new byte[data.length + 5];
        packet[0] = (byte) (data.length & 0xFF);
        packet[1] = (byte) ((data.length >> 8) & 0xFF);
        packet[2] = (byte) ((data.length >> 16) & 0xFF);
        packet[3] = (byte) ((data.length >> 24) & 0xFF);
        packet[4] = cmd.packetId;
        System.arraycopy(data, 0, packet, 5, data.length);
        out.write(packet);
        out.flush();
    }

    public static class BinaryPacket {
        private final int packetId;
        private final byte[] data;

        public BinaryPacket(int packetId, byte[] data) {
            this.packetId = packetId;
            this.data = data;
        }

        public int getPacketId() {
            return packetId;
        }

        public byte[] getData() {
            return data;
        }
