        public int commandType;
        public byte[] arguments;
    }

    public static class MySQLPacket {
        public static final int COM_QUERY = 3;
        public static final int COM_QUIT = 1;
    }

    public static class QuitPacket {
        public static final byte[] DATA = new byte[1];
        static {
            DATA[0] = 0x01;
        }
