public class UDPCommunicator {
    private DatagramSocket socket;
    private boolean isServer;

    public UDPCommunicator(boolean isServer, int port) throws SocketException {
        this.isServer = isServer;
        this.socket = new DatagramSocket(null);
        if (isServer) {
            socket.bind(new InetSocketAddress("0.0.0.0", port));
        }
    }

    public void sendPacket(byte[] data, String destAddress, int destPort) throws IOException {
        if (isServer) {
            throw new IllegalStateException("Server cannot send packets without first receiving");
        }
        DatagramPacket packet = new DatagramPacket(data, data.length, 
            new InetSocketAddress(destAddress, destPort));
        socket.send(packet);
    }

    public byte[] receivePacket() throws IOException {
        if (!isServer) {
            throw new IllegalStateException("Client should not be receiving packets");
        }
        byte[] buffer = new byte[800];
        DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
        socket.receive(packet);
        return packet.getData();
    }

    public void close() {
        if (socket != null && !socket.isClosed()) {
            socket.close();
        }
    }
}

