            int responseCode = headConnection.getResponseCode();
            if (responseCode == HttpURLConnection.HTTP_MOVED_PERM || responseCode == HttpURLConnection.HTTP_MOVED_TEMP) {
                String newLocation = headConnection.getHeaderField("Location");
                headConnection = (HttpURLConnection) new URL(newLocation).openConnection();
                headConnection.setRequestMethod("HEAD");
                headConnection.setInstanceFollowRedirects(false);
                headConnection.setDoInput(true);
                responseCode = headConnection.getResponseCode();
            }
            sourceInfo.setLength(getContentLength(headConnection));
            sourceInfo.setMimeType(headConnection.getContentType());
            if (sourceInfoStorage != null) {
                sourceInfoStorage.saveSourceInfo(sourceInfo);
            }
        }
    }
}

