    public KafkaTopicCreator(KafkaProperties kafkaProperties) {
        this.adminClientProperties = kafkaProperties.buildAdminProperties(null);
    }

    public void createTopic(String topicName, int partitions, short replicationFactor) throws Exception {
        try (AdminClient adminClient = AdminClient.create(this.adminClientProperties)) {
            NewTopic newTopic = new NewTopic(topicName, partitions, replicationFactor);
