const express = require('express');
const fs = require('fs');
const path = require('path');
const responseTime = require('response-time');

const app = express();
const port = 3000;
const imagePath = path.join(__dirname, 'CapitalOne_Digital_Skills_Infographic_BW.jpg');

app.use(responseTime());

app.get('/stream', (req, res) => {
  const stream = fs.createReadStream(imagePath);
  stream.pipe(res);
});

app.get('/non-stream', (req, res) => {
  const fileData = fs.readFileSync(imagePath);
  res.send(fileData);
});

app.get('/stream-buffer', (req, res) => {
  const stream = fs.createReadStream(imagePath);
  const chunks = [];
  stream.on('data', (chunk) => {
    chunks.push(chunk);
  });
  stream.on('end', () => {
    res.send(Buffer.concat(chunks));
  });
});

app.get('/stream-partial', (req, res) => {
  const stream = fs.createReadStream(imagePath);
  stream.pipe(res);
  stream.on('data', (chunk) => {
    if (res.headersSent) {
      stream.unpipe(res);
    }
  });
});

app.listen(port, () => {
