function createServer() {
  const server = http.createServer((req, res) => {
    const url = req.url;
    const method = req.method;

    if (url === '/get' && method === 'GET') {
      res.writeHead(200, { 'Content-Type': 'text/plain' });
      res.end('content');
    } else if (url === '/put' && method === 'PUT') {
      let data = '';
      req.on('data', chunk => {
        data += chunk.toString();
      });
      req.on('end', () => {
        if (data === 'content') {
          res.writeHead(200, { 'Content-Type': 'text/plain' });
          res.end('success');
        } else {
          res.writeHead(400, { 'Content-Type': 'text/plain' });
          res.end('Bad Request');
        }
      });
    } else if (url === '/proxy' && method === 'PUT') {
      const proxyReq = http.request({
        hostname: 'localhost',
        port: 3000,
        path: '/put',
        method: 'PUT'
      });
      req.pipe(proxyReq);
      proxyReq.on('response', proxyRes => {
        proxyRes.pipe(res);
      });
    } else if (url === '/test' && method === 'GET') {
      const getReq = http.request({
        hostname: 'localhost',
        port: 3000,
        path: '/get',
        method: 'GET'
      });
      getReq.on('response', getRes => {
        const proxyReq = http.request({
          hostname: 'localhost',
