const http = require('http');
const fs = require('fs');
const path = require('path');
const send = require('send');
const marked = require('marked');

class Server {
  constructor(config) {
    this.config = config;
    this._http = null;
    this.sockets = [];
    this.path = config.path;
    this.updateServer();
  }

  updateServer() {
    this._http = http.createServer((req, res) => {
      const url = req.url === '/' ? '/index.html' : req.url;
      const filePath = path.join(this.path, url);
      const stat = fs.statSync(filePath);

      if (stat.isDirectory()) {
        const indexFile = path.join(filePath, 'index.html');
        if (fs.existsSync(indexFile)) {
          filePath = indexFile;
        } else {
          const files = fs.readdirSync(filePath);
          const html = marked(files.map(file => `- [${file}](${url}/${file})`).join('\n'));
          res.writeHead(200, { 'Content-Type': 'text/html' });
          res.end(html);
          return;
        }
      }

      send(req, filePath)
        .on('error', err => {
          if (err.code === 'EISDIR') {
            res.statusCode = 301;
            res.setHeader('Location', url + '/');
            res.end();
          } else {
            res.statusCode = 404;
            res.end('File not found');
          }
        })
        .pipe(res);
    });
  }

  start(callback) {
    this._http.listen(this.config.port, callback);
  }

  stop(callback) {
    this._http.close(() => {
      this.sockets.forEach(socket => socket.end());
      callback();
    });
  }
}
