const net = require('net');
const EventEmitter = require('events');

class TcpReader extends EventEmitter {
  constructor(opts, transporter, Promise, logger) {
    super();
    this.server = null;
    this.opts = opts;
    this.transporter = transporter;
    this.Promise = Promise;
    this.logger = logger;
    this.sockets = [];
  }

  listen() {
    return new this.Promise((resolve, reject) => {
      this.server = net.createServer();
      this.server.on('listening', () => {
        const address = this.server.address();
        this.logger.info(`TCP server listening on ${address.address}:${address.port}`);
        resolve(address.port);
      });
      this.server.on('connection', this.onTcpClientConnected.bind(this));
      this.server.on('error', (err) => {
        this.logger.error('TCP server error:', err);
        reject(err);
      });
      this.server.listen(this.opts.port);
    });
  }

  onTcpClientConnected(socket) {
    this.logger.info(`New TCP client connected: ${socket.remoteAddress}:${socket.remotePort}`);
    this.sockets.push(socket);
    socket.on('data', (data) => {
      try {
        const parsedData = this.transporter.parser.parse(data);
        this.emit('data', parsedData);
      } catch (err) {
        this.logger.error('TCP data parsing error:', err);
      }
    });
    socket.on('error', (err) => {
      this.logger.error(`TCP client error: ${socket.remoteAddress}:${socket.remotePort}`, err);
      this.closeSocket(socket);
    });
    socket.on('close', () => {
      this.logger.info(`TCP client disconnected: ${socket.remoteAddress}:${socket.remotePort}`);
      this.closeSocket(socket);
    });
  }

  closeSocket(socket) {
    const index = this.sockets.indexOf(socket);
    if (index !== -1) {
      this.sockets.splice(index, 1);
    }
    socket.end();
    socket.destroy();
  }

  close() {
    return new this.Promise((resolve) => {
      this.server.close(() => {
        this.sockets.forEach((socket) => {
          this.closeSocket(socket);
        });
        this.sockets = [];
        this.logger.info('TCP server closed');
        resolve();
      });
    });
  }
}
