const net = require('net');
const EventEmitter = require('events');

class Node extends EventEmitter {
  constructor(id) {
    super();
    this.id_ = id;
    this.peers_ = {};
    this.PORT = 3000;
    this.server_ = net.createServer();
    this.server_.on('connection', (socket) => {
      socket.on('data', (data) => this.on_data(data, socket));
      socket.on('close', () => this.remove_peer(socket));
    });
  }

  start() {
    return new Promise((resolve, reject) => {
      this.server_.listen(this.PORT + this.id_, () => {
        console.log(`Node ${this.id_} listening on port ${this.PORT + this.id_}`);
        resolve();
      });
    });
  }

  on_data(data, socket) {
    const message = JSON.parse(data);
    this.emit('message', message, socket);
  }

  send(socket, data) {
    socket.write(JSON.stringify(data));
  }

  broadcast(data) {
    for (const peer in this.peers_) {
      this.send(this.peers_[peer], data);
    }
  }

  add_peer(socket, remote_id) {
    this.peers_[remote_id] = socket;
  }

  remove_peer(socket) {
    for (const peer in this.peers_) {
      if (this.peers_[peer] === socket) {
        delete this.peers_[peer];
        break;
      }
    }
  }

  list_peers() {
    return Object.keys(this.peers_);
  }
}
