const dgram = require('dgram');
const { EventEmitter } = require('events');

function setupUDPServer(argv, io, debug) {
  const server = dgram.createSocket('udp4');
  const streams = new Map();
  const MAX_MESSAGES = 100;

  server.on('message', (msg, rinfo) => {
    try {
      const data = JSON.parse(msg);
      const { stream, message } = data;

      if (!stream || !message) {
        debug('Invalid message format:', msg);
        return;
      }

      if (!streams.has(stream)) {
        streams.set(stream, []);
      }

      const streamData = streams.get(stream);
      streamData.push(message);

      if (streamData.length > MAX_MESSAGES) {
        streamData.shift();
      }

      io.emit('newMessage', { stream, message });
      io.emit('updateStreams', Array.from(streams.keys()));
    } catch (err) {
      debug('Error parsing message:', err);
    }
  });

  server.on('listening', () => {
    const address = server.address();
    debug(`UDP server listening on ${address.address}:${address.port}`);
  });

  server.bind(argv.udpPort, argv.udpHost);
}
