class ServerDiscovery {
  constructor() {
    this.nearbyServers = {};
    this.lastDiscoveryBroadcast = 0;
    this.broadcastIP = '';
    this.discoveryListener = null;
  }

  setupDiscovery(callback) {
    const dgram = require('dgram');
    const { networkInterfaces } = require('os');
    const interfaces = networkInterfaces();
    const addresses = Object.keys(interfaces).reduce((result, name) => {
      interfaces[name].forEach((iface) => {
        if (iface.family === 'IPv4' && !iface.internal) {
          result.push(iface.address);
        }
      });
      return result;
    }, []);

    this.broadcastIP = addresses[0] || '255.255.255.255';
    this.discoveryListener = dgram.createSocket('udp4');
    this.discoveryListener.on('listening', () => {
      const address = this.discoveryListener.address();
      console.log(`Discovery server listening on ${address.address}:${address.port}`);
      if (callback) callback();
    });
    this.discoveryListener.on('message', (msg, rinfo) => {
      this.discoveryReceive(msg, rinfo);
    });
    this.discoveryListener.bind(41234);
  }

  discoveryBroadcast(type, message, callback) {
    const dgram = require('dgram');
    const client = dgram.createSocket('udp4');
    const msg = JSON.stringify({ type, message });
    client.send(msg, 0, msg.length, 41234, this.broadcastIP, (err) => {
      if (err) console.error(err);
      if (callback) callback();
      client.close();
    });
  }

  discoveryReceive(msg, rinfo) {
    const data = JSON.parse(msg);
    if (data.type === 'heartbeat') {
      this.nearbyServers[rinfo.address] = data.message;
    }
  }

  shutdownDiscovery() {
    if (this.discoveryListener) {
      this.discoveryListener.close();
      this.discoveryListener = null;
    }
  }

  calcBroadcastIP() {
    // Implementation not shown, but would involve determining the local network interface and calculating the broadcast address
  }
}
