const WebSocket = require('ws');

class WebSocketClient {
  constructor() {
    this.ws = null;
    this.executorMap = new Map();
    this.bufferMap = new Map();
    this.timer = null;
  }

  async connect() {
    return new Promise((resolve, reject) => {
      this.ws = new WebSocket('wss://speech.platform.bing.com/consumer/speech/synthesize/readaloud/edge/v1', {
        headers: {
          'Content-Type': 'application/ssml+xml',
          'X-Microsoft-OutputFormat': 'riff-24khz-16bit-mono-pcm',
          'User-Agent': 'YOUR_USER_AGENT_HERE',
          'Authorization': 'Bearer YOUR_ACCESS_TOKEN_HERE',
        },
      });

      this.ws.on('open', () => {
        clearTimeout(this.timer);
        resolve(this.ws);
      });

      this.ws.on('error', (err) => {
        reject(err);
      });

      this.ws.on('close', () => {
        this.ws = null;
        this.executorMap.clear();
        this.bufferMap.clear();
      });

      this.ws.on('message', (data) => {
        const requestId = data.toString().match(/"requestId":"(.*?)"/)[1];
        if (data[0] === 0x02) {
          this.bufferMap.set(requestId, []);
        } else if (data[0] === 0x00) {
          const buffer = Buffer.concat(this.bufferMap.get(requestId));
          this.executorMap.get(requestId).resolve(buffer);
          this.bufferMap.delete(requestId);
          this.executorMap.delete(requestId);
        } else {
          this.bufferMap.get(requestId).push(data);
        }
      });

      this.timer = setTimeout(() => {
        reject(new Error('Connection timeout'));
      }, 5000);
    });
  }

  async sendRequest(ssml, format) {
    return new Promise((resolve, reject) => {
      if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
        reject(new Error('WebSocket connection is not open'));
        return;
      }

      const requestId = Math.random().toString(36).substr(2, 9);
      this.executorMap.set(requestId, { resolve, reject });

      const message = JSON.stringify({
        requestId,
        stream: 'start',
        voiceName: 'en-US-AriaRUS',
        format,
        ssml: `<speak version="1.0" xmlns="http://www.w3.org/2001/10/synthesis" xml:lang="en-US"><voice name="en-US-AriaRUS">${ssml}</voice></speak>`,
      });

      this.ws.send(message);
    });
  }
}
