                event.isReconnect = wasReconnect;
                wasReconnect = false;
                eventTarget.dispatchEvent(event);
            };

            ws.onclose = (event) => {
                clearTimeout(timeout);
                ws = null;
                if (forcedClose) {
                    this.readyState = WebSocket.CLOSED;
                    eventTarget.dispatchEvent(this.createEvent('close'));
                } else {
                    this.readyState = WebSocket.CONNECTING;
                    var connectingEvent = this.createEvent('connecting');
                    connectingEvent.code = event.code;
                    connectingEvent.reason = event.reason;
