class ReconnectingWebSocket {
  constructor(url, protocols, options) {
    this.debug = options.debug || false;
    this.automaticOpen = options.automaticOpen || true;
    this.reconnectInterval = options.reconnectInterval || 1000;
    this.maxReconnectInterval = options.maxReconnectInterval || 30000;
    this.reconnectDecay = options.reconnectDecay || 1.5;
    this.timeoutInterval = options.timeoutInterval || 5000;
    this.url = url;
    this.protocols = protocols;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CLOSED;
    this.protocol = null;
    this._listeners = {};
    this._connectTimeout = null;
    this._reconnectInterval = this.reconnectInterval;
    this._websocket = null;
    this._connect();
  }

  addEventListener(type, listener) {
    if (!this._listeners[type]) {
      this._listeners[type] = [];
    }
    this._listeners[type].push(listener);
  }

  removeEventListener(type, listener) {
    if (!this._listeners[type]) {
      return;
    }
    const index = this._listeners[type].indexOf(listener);
    if (index > -1) {
      this._listeners[type].splice(index, 1);
    }
  }

  dispatchEvent(event) {
    if (this._listeners[event.type]) {
      this._listeners[event.type].forEach(listener => listener(event));
    }
  }

  open(wasReconnect) {
    if (this.debug) {
      console.log('ReconnectingWebSocket: Open');
    }
    this._websocket = new WebSocket(this.url, this.protocols);
    this._websocket.binaryType = 'arraybuffer';
    this._websocket.onopen = event => {
      this.readyState = WebSocket.OPEN;
      this.reconnectAttempts = 0;
      this._reconnectInterval = this.reconnectInterval;
      clearTimeout(this._connectTimeout);
      if (wasReconnect) {
        const event = this.createEvent('reconnect');
        this.dispatchEvent(event);
      }
      const event = this.createEvent('open');
      this.dispatchEvent(event);
    };
    this._websocket.onclose = event => {
      this.readyState = WebSocket.CLOSED;
      clearTimeout(this._connectTimeout);
      const event = this.createEvent('close');
      this.dispatchEvent(event);
      if (this.automaticOpen) {
        this._reconnectInterval = Math.min(this._reconnectInterval * this.reconnectDecay, this.maxReconnectInterval);
        this.reconnectAttempts++;
        this._connectTimeout = setTimeout(() => this.open(true), this._reconnectInterval);
      }
    };
    this._websocket.onconnecting = event => {
      this.readyState = WebSocket.CONNECTING;
      const event = this.createEvent('connecting');
      this.dispatchEvent(event);
    };
    this._websocket.onmessage = event => {
      const event = this.createEvent('message', event);
      this.dispatchEvent(event);
    };
    this._websocket.onerror = event => {
      const event = this.createEvent('error', event);
      this.dispatchEvent(event);
    };
  }

  send(data) {
    if (this.readyState === WebSocket.OPEN) {
      this._websocket.send(data);
    }
  }

  close(code, reason) {
    if (this._websocket) {
      this._websocket.close(code, reason);
    }
  }

  refresh() {
    this.close();
  }

  createEvent(eventName, detail) {
    return new CustomEvent(eventName, { detail });
  }

  onopen(event) {}
  onclose(event) {}
  onconnecting(event) {}
  onmessage(event) {}
  onerror(event) {}
}
