    const data = this._formatData(method, params, id);
    this._handles[id] = { success: successCallback, error: errorCallback };
    this._send(data);
  }

  batchRequest(requests, successCallback, errorCallback) {
    const data = requests.map((request, index) => this._formatData(request.method, request.params, index));
    const handles = requests.map((request, index) => ({ success: request.success, error: request.error }));
    this._handles = handles.reduce((acc, handle, index) => {
      acc[index] = handle;
      return acc;
    }, {});
    this._send(data);
  }

