class WebSocketClient {
  constructor(url) {
    this.DISCONNECT_TIMEOUT = 30000;
    this.RECONNECT_TIMEOUT = 5000;
    this.wsState = WebSocket.CLOSED;
    this.ws = null;
    this.wsURL = url;
    this.connectTS = 0;
    this.disconnectTID = null;
    this.reconnectTID = null;
    this.onmessage = null;
  }

  onconnect() {
    if (this.wsState === WebSocket.OPEN) {
      return false;
    }
    this.connectTS = Date.now();
    this.ws = new WebSocket(this.wsURL);
    this.ws.onopen = this.onopen.bind(this);
    this.ws.onclose = this.onclose.bind(this);
    this.ws.onmessage = this.onmessage.bind(this);
    this.ws.onerror = this.ondisconnect.bind(this);
    return true;
  }

  ondisconnect() {
    this.wsState = WebSocket.CLOSED;
    this.ws = null;
    if (this.disconnectTID) {
      clearTimeout(this.disconnectTID);
    }
    this.disconnectTID = setTimeout(this.ondisconnect.bind(this), this.DISCONNECT_TIMEOUT);
  }

  onopen(e) {
    this.wsState = WebSocket.OPEN;
    if (this.disconnectTID) {
      clearTimeout(this.disconnectTID);
    }
    if (this.reconnectTID) {
      clearTimeout(this.reconnectTID);
    }
  }

  onclose() {
    this.wsState = WebSocket.CLOSED;
    if (this.disconnectTID) {
      clearTimeout(this.disconnectTID);
    }
    if (this.reconnectTID) {
      clearTimeout(this.reconnectTID);
    }
    this.reconnectTID = setTimeout(this.reconnect.bind(this), this.RECONNECT_TIMEOUT);
  }

  send(e) {
    if (this.wsState === WebSocket.OPEN) {
      this.ws.send(JSON.stringify(e));
    }
  }

  reconnect() {
    this.ondisconnect();
    this.onconnect();
  }
}
