const axios = require('axios');

class QuqiClient {
  constructor(config) {
    this.headers = {
      Cookie: config.cookies,
      Referer: 'https://quqi.com/',
    };
    this.config = config;
  }

  async _sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  async getDailyTask() {
    try {
      const response = await axios.get('https://quqi.com/api/task/daily', {
        headers: this.headers,
      });
      return response.data;
    } catch (error) {
      console.error('Error fetching daily tasks:', error);
      return null;
    }
  }

  async getReward(id) {
    try {
      await axios.post('https://quqi.com/api/task/reward', { id }, {
        headers: this.headers,
      });
    } catch (error) {
      console.error('Error claiming reward:', error);
    }
  }

  async visit(id) {
    try {
      await axios.post('https://quqi.com/api/task/visit', { id }, {
        headers: this.headers,
      });
    } catch (error) {
      console.error('Error visiting group:', error);
    }
  }

  async getQuqiId() {
    try {
      const response = await axios.get('https://quqi.com/api/group', {
        headers: this.headers,
      });
      const group = response.data;
      if (!group.is_member) {
        await axios.post('https://quqi.com/api/group/join', { id: group.id }, {
          headers: this.headers,
        });
      }
      return group;
    } catch (error) {
      console.error('Error getting group information:', error);
      return null;
    }
  }

  async uploadImg(id) {
    try {
      const response = await axios.post('https://quqi.com/api/task/upload', { id }, {
        headers: this.headers,
      });
      await axios.post('https://quqi.com/api/task/delete', { id: response.data.id }, {
        headers: this.headers,
      });
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  }

  async executeTasks() {
    const tasks = await this.getDailyTask();
    if (!tasks) return null;
    for (const task of tasks) {
      switch (task.type) {
        case 'reward':
          await this.getReward(task.id);
          break;
        case 'visit':
          await this.visit(task.id);
          break;
        case 'upload':
          await this.uploadImg(task.id);
          break;
        default:
          console.log(`Unknown task type: ${task.type}`);
      }
      await this._sleep(1000);
    }
    const response = await axios.get('https://quqi.com/api/wallet', {
      headers: this.headers,
    });
    return response.data;
  }
}
