class ReconnectingWebSocket {
  constructor(url, protocols, options = {}) {
    this.url = url;
    this.protocols = protocols;
    this.debug = options.debug || false;
    this.automaticOpen = options.automaticOpen !== false;
    this.reconnectInterval = options.reconnectInterval || 1000;
    this.maxReconnectInterval = options.maxReconnectInterval || 30000;
    this.reconnectDecay = options.reconnectDecay || 1.5;
    this.timeoutInterval = options.timeoutInterval || 2000;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CLOSED;
    this.protocol = null;
    this._listeners = {};
    this._connectTimeout = null;
    this._reconnectInterval = null;
    this._socket = null;
    this._connect();
  }

  addEventListener(type, listener) {
    if (!this._listeners[type]) {
      this._listeners[type] = [];
    }
    this._listeners[type].push(listener);
  }

  removeEventListener(type, listener) {
    if (!this._listeners[type]) {
      return;
    }
    const index = this._listeners[type].indexOf(listener);
    if (index > -1) {
      this._listeners[type].splice(index, 1);
    }
  }

  dispatchEvent(event) {
    if (this._listeners[event.type]) {
      this._listeners[event.type].forEach(listener => listener(event));
    }
  }

  _connect() {
    if (this.debug) {
      console.log('ReconnectingWebSocket: connecting...');
    }
    this.readyState = WebSocket.CONNECTING;
    this.dispatchEvent({ type: 'connecting' });
    this._socket = new WebSocket(this.url, this.protocols);
    this._socket.binaryType = 'arraybuffer';
    this._socket.onopen = () => this._onopen();
    this._socket.onclose = () => this._onclose();
    this._socket.onmessage = event => this._onmessage(event);
    this._socket.onerror = event => this._onerror(event);
    this._startConnectTimeout();
  }

  _startConnectTimeout() {
    this._clearConnectTimeout();
    this._connectTimeout = setTimeout(() => {
      if (this.debug) {
        console.log('ReconnectingWebSocket: connection timeout');
      }
      this._onerror(new Error('Connection timeout'));
    }, this.timeoutInterval);
  }

  _clearConnectTimeout() {
    clearTimeout(this._connectTimeout);
  }

  _onopen() {
    if (this.debug) {
      console.log('ReconnectingWebSocket: connected');
    }
    this._clearConnectTimeout();
    this.readyState = WebSocket.OPEN;
    this.reconnectAttempts = 0;
    this.protocol = this._socket.protocol;
    this.dispatchEvent({ type: 'open' });
  }

  _onclose() {
    if (this.debug) {
      console.log('ReconnectingWebSocket: closed');
    }
    this._clearConnectTimeout();
    this.readyState = WebSocket.CLOSED;
    this.protocol = null;
    this.dispatchEvent({ type: 'close' });
    if (this.automaticOpen) {
      this._scheduleReconnect();
    }
  }

  _onmessage(event) {
    this.dispatchEvent({ type: 'message', data: event.data });
  }

  _onerror(event) {
    if (this.debug) {
      console.log('ReconnectingWebSocket: error', event);
    }
    this._clearConnectTimeout();
    this.readyState = WebSocket.CLOSED;
    this.protocol = null;
    this.dispatchEvent({ type: 'error', error: event });
    if (this.automaticOpen) {
      this._scheduleReconnect();
    }
  }

  _scheduleReconnect() {
    this.reconnectAttempts++;
    const delay = Math.min(this.maxReconnectInterval, this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts));
    if (this.debug) {
      console.log(`ReconnectingWebSocket: scheduling reconnect in ${delay} ms`);
    }
    this._reconnectInterval = setTimeout(() => this._connect(), delay);
  }

  send(data) {
    if (this.readyState === WebSocket.OPEN) {
      this._socket.send(data);
    } else {
      throw new Error('WebSocket is not open.');
    }
  }

  close(code, reason) {
    this.automaticOpen = false;
    if (this._reconnectInterval) {
      clearTimeout(this._reconnectInterval);
    }
    this._clearConnectTimeout();
    if (this.readyState === WebSocket.CONNECTING) {
      this._socket.close();
    } else {
      this.readyState = WebSocket.CLOSED;
      this.protocol = null;
      this.dispatchEvent({ type: 'close' });
    }
  }

  refresh() {
    this.close();
    this._connect();
  }

  onopen() {}
  onclose() {}
  onconnecting() {}
  onmessage() {}
  onerror() {}
}
