    this.sock = null;
    this.conf = null;
    this.onready = null;
  }

  onclose(ev) {
    this.alerts.error('WebSocket closed: ' + ev);
    this.handles.forEach((handle) => {
      handle.reject(new Error('WebSocket closed'));
    });
    this.handles = [];
    this.initialized = false;
    this.sock = null;
    this.conf = null;
    this.onready = null;
  }

  onopen() {
    this.alerts.info('WebSocket opened');
    this.initialized = true;
    if (this.onready) {
      this.onready();
    }
  }

  onmessage(message) {
    const data = this.JSON.parse(message.data);
    if (data.id) {
      const handle = this.handles[data.id];
      if (handle) {
        if (data.error) {
          handle.reject(data.error);
        } else {
          handle.resolve(data.result);
        }
        this.handles[data.id] = null;
      }
    } else {
      this.alerts.info('WebSocket message: ' + message.data);
    }
  }

  invoke(opts) {
    const id = this.utils.uuid();
    const request = {
      jsonrpc: '2.0',
      id: id,
      method: opts.name,
      params: opts.params,
    };
    const handle = {
      resolve: opts.success,
      reject: opts.error,
    };
    this.handles[id] = handle;
    this.sock.send(this.JSON.stringify(request));
  }

  init(conf, onready) {
    this.conf = conf;
    this.onready = onready;
    const url = this.conf.host + ':' + this.conf.port;
    if (this.conf.path) {
      url += '/' + this.conf.path;
    }
    if (this.conf.encrypt) {
      this.scheme = 'wss';
    }
    const ws = new WebSocket(this.scheme + '://' + url);
    ws.onerror = this.onerror.bind(this);
    ws.onclose = this.onclose.bind(this);
    ws.onopen = this.onopen.bind(this);
    ws.onmessage = this.onmessage.bind(this);
    this.sock = ws;
  }
}
