  private webSocket: WebSocket | undefined;
  private wsPort: number | undefined;

  constructor() {
    this.webSocket = undefined;
    this.wsPort = undefined;
  }

  async connect(addressPort: number): Promise<void> {
    return new Promise((resolve, reject) => {
      this.webSocket = new WebSocket(`ws://localhost:${addressPort}`);
      this.wsPort = addressPort;

      this.webSocket.onopen = () => {
        resolve();
      };

      this.webSocket.onerror = (error) => {
        reject(error);
      };

      this.webSocket.onmessage = (event) => {
        const message = JSON.parse(event.data);
        if (message.command === 'settings') {
          this.settingsChanged(message.data);
        } else if (message.command === 'message') {
          this.messageReceived(message.data);
        }
      };

      this.webSocket.onclose = () => {
        this.webSocket = undefined;
        this.wsPort = undefined;
      };
    });
  }

  disconnect(): void {
    if (this.webSocket) {
      this.webSocket.close();
    }
  }

